import { beforeAll, expect, test } from "vitest";
import { Dwolla } from "../index.js";
import { createDwollaClient, getAccountId } from "./setup.js";

let dwolla: Dwolla;
let accountId: string;

beforeAll(async () => {
  dwolla = createDwollaClient("accounts");
  accountId = await getAccountId();
}, 30000);

// ============================================================================
// ACCOUNTS TESTS
// ============================================================================

test("Accounts Get Account", async () => {
  const result = await dwolla.accounts.get({
    id: accountId,
  });
  expect(result).toBeDefined();
  expect(result.id).toBeDefined();
  expect(result.name).toBeDefined();
  expect(result.links).toBeDefined();
  expect(result.links?.["self"]).toBeDefined();
  expect(typeof result.timezoneOffset).toBe("number");
  expect(result.type).toBeDefined();
});

// ============================================================================
// ACCOUNTS FUNDING SOURCES TESTS
// ============================================================================

test("Accounts List Funding Sources", async () => {
  const result = await dwolla.accounts.fundingSources.list({
    id: accountId,
  });
  expect(result).toBeDefined();
  expect(result.links).toBeDefined();
  expect(result.links?.["self"]).toBeDefined();
  expect(result.embedded).toBeDefined();
  expect(Array.isArray(result.embedded?.fundingSources ?? [])).toBe(true);

  if ((result.embedded?.fundingSources?.length ?? 0) > 0) {
    const firstFs = result.embedded!.fundingSources![0]!;
    expect(firstFs.id).toBeDefined();
    expect(firstFs.status).toBeDefined();
    expect(firstFs.type).toBeDefined();
    expect(firstFs.name).toBeDefined();
    expect(firstFs.links).toBeDefined();
    expect(firstFs.created).toBeInstanceOf(Date);
  }
});

