import { beforeAll, expect, test } from "vitest";
import { Dwolla } from "../index.js";
import { createDwollaClient } from "./setup.js";

let dwolla: Dwolla;
let businessClassificationId: string | undefined;

beforeAll(async () => {
  dwolla = createDwollaClient("businessclassifications");
  
  // List business classifications to get one for testing
  const classifications = await dwolla.businessClassifications.list();
  businessClassificationId = classifications.embedded?.businessClassifications?.[0]?.id;
}, 30000);

test("Businessclassifications List Business Classifications", async () => {
  const result = await dwolla.businessClassifications.list();
  expect(result).toBeDefined();
  expect(result.links).toBeDefined();
  expect(result.embedded).toBeDefined();
  expect(Array.isArray(result.embedded?.businessClassifications ?? [])).toBe(true);
  
  if ((result.embedded?.businessClassifications?.length ?? 0) > 0) {
    const first = result.embedded!.businessClassifications![0]!;
    expect(first.id).toBeDefined();
    expect(first.name).toBeDefined();
  }
});

test("Businessclassifications Retrieve Business Classification", async () => {
  const result = await dwolla.businessClassifications.get({
    id: businessClassificationId!,
  });
  expect(result).toBeDefined();
  expect(result.links).toBeDefined();
  expect(result.embedded).toBeDefined();
  expect(Array.isArray(result.embedded?.industryClassifications ?? [])).toBe(true);
  expect(result.id).toBeDefined();
  expect(result.name).toBeDefined();
  
  if ((result.embedded?.industryClassifications?.length ?? 0) > 0) {
    const first = result.embedded!.industryClassifications![0]!;
    expect(first.id).toBeDefined();
    expect(first.name).toBeDefined();
  }
});
