import { expect, test } from "vitest";
import { Dwolla } from "../index.js";
import { createTestHTTPClient } from "./testclient.js";

test("Customers Exchanges List Customer Exchanges", async () => {
  const testHttpClient = createTestHTTPClient("listCustomerExchanges");

  const dwolla = new Dwolla({
    serverURL: "https://api-sandbox.dwolla.com",
    httpClient: testHttpClient,
    security: {
      clientID: process.env["DWOLLA_CLIENT_ID"] ?? "value",
      clientSecret: process.env["DWOLLA_CLIENT_SECRET"] ?? "value",
    },
  });

  const result = await dwolla.customers.exchanges.list({
    id: "a952ff26-af08-457f-a7da-dd09670c9d98",
  });
  expect(result).toBeDefined();

  // Assert structure rather than exact payload to tolerate real sandbox data
  expect(result.links).toBeDefined();
  expect(result.links?.["self"]).toBeDefined();
  expect(result.embedded).toBeDefined();
  expect(Array.isArray(result.embedded?.exchanges ?? [])).toBe(true);
  expect(typeof result.total).toBe("number");

  // Validate shape of the first exchange if present
  if ((result.embedded?.exchanges?.length ?? 0) > 0) {
    const first = result.embedded!.exchanges![0]!;
    expect(first.id).toBeDefined();
    expect(first.status).toBeDefined();
    expect(first.created).toBeInstanceOf(Date);
    expect(first.links).toBeDefined();
    expect(first.links?.["self"]).toBeDefined();
    expect(first.links?.["customer"]).toBeDefined();
    expect(first.links?.["exchange-partner"]).toBeDefined();
  }
});
