import { expect, test } from "vitest";
import { Dwolla } from "../index.js";
import { createTestHTTPClient } from "./testclient.js";

test("Root Get Root", async () => {
  const testHttpClient = createTestHTTPClient("getRoot");

  const dwolla = new Dwolla({
    serverURL: "https://api-sandbox.dwolla.com",
    httpClient: testHttpClient,
    security: {
      clientID: process.env["DWOLLA_CLIENT_ID"] ?? "value",
      clientSecret: process.env["DWOLLA_CLIENT_SECRET"] ?? "value",
    },
  });

  const result = await dwolla.root.get();
  expect(result).toBeDefined();
  expect(result.links).toBeDefined();
  expect(result.links?.["account"]).toBeDefined();
  expect(result.links?.["customers"]).toBeDefined();
  expect(result.links?.["webhook-subscriptions"]).toBeDefined();
});
