import { expect, test } from "vitest";
import { Dwolla } from "../index.js";
import { createTestHTTPClient } from "./testclient.js";

test("Tokens Create Application Access Token", async () => {
  const testHttpClient = createTestHTTPClient("createApplicationAccessToken");

  const dwolla = new Dwolla({
    serverURL: "https://api-sandbox.dwolla.com",
    httpClient: testHttpClient,
    security: {
      clientID: process.env["DWOLLA_CLIENT_ID"] ?? "value",
      clientSecret: process.env["DWOLLA_CLIENT_SECRET"] ?? "value",
    },
  });

  const clientId = process.env["DWOLLA_CLIENT_ID"] ?? "";
  const clientSecret = process.env["DWOLLA_CLIENT_SECRET"] ?? "";
  const dwollaBasicAuth = Buffer.from(`${clientId}:${clientSecret}`).toString("base64");

  const result = await dwolla.tokens.create({
    basicAuth: "Basic" + dwollaBasicAuth,
  }, {
    grantType: "client_credentials",
  });

  expect(result).toBeDefined();
  // Assert structure rather than exact payload
  expect(result.accessToken).toBeDefined();
  expect(result.tokenType).toBeDefined();
  expect(typeof result.expiresIn).toBe("number");
});
