import { afterAll, beforeAll, expect, test } from "vitest";
import { Dwolla } from "../index.js";
import { createDwollaClient, createUnverifiedCustomer, extractIdFromLocationHeader } from "./setup.js";

let dwolla: Dwolla;
let subscriptionId: string | undefined;
let webhookId: string | undefined;

beforeAll(async () => {
  dwolla = createDwollaClient("webhooks");
  
  // Create a single webhook subscription for all tests
  const subscriptionResult = await dwolla.webhookSubscriptions.create({
    url: "http://myapplication.com/webhooks-consolidated-test",
    secret: "consolidatedtestsecret",
  });
  
  subscriptionId = extractIdFromLocationHeader(subscriptionResult?.headers);
  
  // create a unverified customer to trigger a webhook
  await createUnverifiedCustomer(dwolla);

  // Try to find an existing webhook from the subscription
  if (subscriptionId) {
    const webhooks = await dwolla.webhookSubscriptions.webhooks.list({
      id: subscriptionId,
    });
    webhookId = webhooks.embedded?.webhooks?.[0]?.id;
  }
}, 30000);

afterAll(async () => {
  // Clean up: delete the subscription
  if (subscriptionId) {
    try {
      await dwolla.webhookSubscriptions.delete({ id: subscriptionId });
    } catch {
      // Ignore cleanup errors
    }
  }
}, 30000);

